import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link as ScrollLink } from 'react-scroll';
const AdminDashboard = () => {
  const [eventName, setEventName] = useState('');
  const [eventDate, setEventDate] = useState('');
  const [eventLink, setEventLink] = useState('');
  const [eventDescription, setEventDescription] = useState('');
  const [selectedSection, setSelectedSection] = useState(null);

  useEffect(() => {
    // Fetch events when the component mounts
    const fetchEvents = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/events");
        setEvents(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchEvents();
  }, []); // Run the effect only once when the component mounts




  const handleAddEvent = async () => {
    try {
      // Send a POST request to your server to add a new event
      await axios.post('http://localhost:5000/api/add-event', {
        eventName,
        eventDate,
        eventLink,
        description: eventDescription,
      });
      alert('Event added successfully!');
    } catch (error) {
      console.error(error);
      alert('Failed to add event. Please try again.');
    }
  };

  return (
    <div>
      <h2>Welcome to the Admin Dashboard!</h2>

      {/* Add your admin dashboard content here */}
      <div className="section-links">
        <Link to="/admin/add-new-event">Add New Event</Link>
        <Link to="/admin/edit-existing-event">Edit Existing Event</Link>
        <Link to="/admin/announce-results">Announce Results</Link>
      </div>

      <h3>Add New Event</h3>
      <label>Event Name:</label>
      <input type="text" value={eventName} onChange={(e) => setEventName(e.target.value)} />

      <label>Event Date:</label>
      <input type="text" value={eventDate} onChange={(e) => setEventDate(e.target.value)} />

      <label>Event Link:</label>
      <input type="text" value={eventLink} onChange={(e) => setEventLink(e.target.value)} />

      <label>Event Description:</label>
      <textarea
        value={eventDescription}
        onChange={(e) => setEventDescription(e.target.value)}
      ></textarea>

      <button onClick={handleAddEvent}>Add Event</button>
    </div>
  );
};

export default AdminDashboard;
