import React, { useState, useEffect } from "react";
import "./App.css";
import { useNavigate } from "react-router-dom";
import {
  Link as ScrollLink,
  Element,
  Events,
  animateScroll as scroll,
  scrollSpy,
} from "react-scroll";
import LoginComp from "./loginComp";
import axios from "axios";

function App() {
  const [currentImage, setCurrentImage] = useState(1);
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [selectedSection, setSelectedSection] = useState(null);
  const navigate = useNavigate();
  const [events, setEvents] = useState([]);

  const [upcomingEvents, setUpcomingEvents] = useState([]);
  const [results, setResults] = useState([]);

  const handleLoginClick = () => {
    navigate("/login");
  };

  useEffect(() => {
    const fetchUpcomingEvents = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/upcoming-events");
        setUpcomingEvents(response.data);
      } catch (error) {
        console.error("Error fetching upcoming events", error);
      }
    };

    const fetchResults = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/results");
        setResults(response.data);
      } catch (error) {
        console.error("Error fetching results", error);
      }
    };

    fetchUpcomingEvents();
    fetchResults();
  }, []);






  return (
    <div>
      <nav className="navbar">
        <ul className="navbar-menu">
          <li>
            <button className="butt" onClick={handleLoginClick}>
              AdminLogin
            </button>
          </li>
        </ul>
        <img
          src="https://www.mirlabs.org/ias22/images/viewfile.jpg"
          alt="Logo"
          className="logo"
          height="50px"
          width="220px"
        />
      </nav>

      <Element name="home" className="element">
        <div className="full-screen-background">
          <div className="image-overlay">
            <h1>Welcome to Programming Club</h1>
          </div>
          <img
            src={`${currentImage}.jpg`}
            alt={`coding-event-${currentImage}`}
          />
        </div>
      </Element>

      {/* Display Upcoming Events */}
      <div className="section">
        <h2>Upcoming Events</h2>
        {upcomingEvents.length === 0 ? (
          <p>No upcoming events</p>
        ) : (
          <ul>
            {upcomingEvents.map((event) => (
              <li key={event._id}>
                <strong>{event.eventName}</strong> - {event.eventDate}
                <br />
                Link: {event.eventLink}
                <br />
                Description: {event.description}
              </li>
            ))}
          </ul>
        )}
      </div>

      {/* Display Results */}
      <div className="section">
        <h2>Results</h2>
        {results.length === 0 ? (
          <p>No results available</p>
        ) : (
          <ul>
            {results.map((result) => (
              <li key={result._id}>
                <strong>{result.eventName}</strong> - Result Link: {result.resultLink}
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}

export default App;
