import React, { useState, useEffect } from "react";
import "./App.css";
import { useNavigate } from "react-router-dom";
import {
  Link as ScrollLink,
  Element,
  Events,
  animateScroll as scroll,
  scrollSpy,
} from "react-scroll";
import LoginComp from "./loginComp";
import EventTable from "./EventTable";
import axios from "axios";

function App() {
  const [currentImage, setCurrentImage] = useState(1);
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [selectedSection, setSelectedSection] = useState(null);
  const navigate = useNavigate();
  const [events, setEvents] = useState([]);

  const handleLoginClick = () => {
    navigate("/login");
  };

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImage((prevImage) => (prevImage % 3) + 1);
    }, 5000);

    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    Events.scrollEvent.register("begin", function () {
      console.log("begin", arguments);
    });

    Events.scrollEvent.register("end", function () {
      console.log("end", arguments);
    });

    scrollSpy.update();

    return () => {
      Events.scrollEvent.remove("begin");
      Events.scrollEvent.remove("end");
    };
  }, []);

  const scrollToTop = () => {
    scroll.scrollToTop();
  };

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const handleSectionClick = (section) => {
    setSelectedSection(section);
  };

  const pastEvents = [
    { name: "Event 1", link: "Link 1" },
    { name: "Event 2", link: "Link 2" },
  ];

  const upcomingEvents = [
    { name: "Upcoming Event 1", link: "Upcoming Link 1" },
    { name: "Upcoming Event 2", link: "Upcoming Link 2" },
  ];

  const results = [
    { name: "Result Event 1", link: "Result Link 1" },
    { name: "Result Event 2", link: "Result Link 2" },
  ];

  return (
    <div>
      <nav className="navbar">
        <ul className="navbar-menu">
          <li>
            <button className="butt" onClick={handleLoginClick}>
              AdminLogin
            </button>
          </li>
        </ul>
        <img
          src="https://www.mirlabs.org/ias22/images/viewfile.jpg"
          alt="Logo"
          className="logo"
          height="50px"
          width="220px"
        />
      </nav>

      <Element name="home" className="element">
        <div className="full-screen-background">
          <div className="image-overlay">
            <h1>Welcome to Programming Club</h1>
          </div>
          <img
            src={`${currentImage}.jpg`}
            alt={`coding-event-${currentImage}`}
          />
        </div>
      </Element>

      <div className="section-links">
        <ScrollLink
          to="pastEvents"
          smooth={true}
          duration={500}
          onClick={() => handleSectionClick("pastEvents")}
          className="section-link"
        >
          Past Events
        </ScrollLink>
        <ScrollLink
          to="upcomingEvents"
          smooth={true}
          duration={500}
          onClick={() => handleSectionClick("upcomingEvents")}
          className="section-link"
        >
          Upcoming Events
        </ScrollLink>
        <ScrollLink
          to="results"
          smooth={true}
          duration={500}
          onClick={() => handleSectionClick("results")}
          className="section-link"
        >
          Results
        </ScrollLink>
      </div>

      {selectedSection === "pastEvents" && <EventTable events={pastEvents} />}
      {selectedSection === "upcomingEvents" && (
        <EventTable events={upcomingEvents} />
      )}
      {selectedSection === "results" && <EventTable events={results} />}
    </div>
  );
}

export default App;
