import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link as ScrollLink } from "react-scroll";

const AdminDashboard = () => {
  const [eventName, setEventName] = useState("");
  const [eventDate, setEventDate] = useState("");
  const [eventLink, setEventLink] = useState("");
  const [eventDescription, setEventDescription] = useState("");
  const [selectedSection, setSelectedSection] = useState(null);

  const handleAddEvent = async () => {
    try {
      // Send a POST request to your server to add a new event
      await axios.post("http://localhost:5000/api/add-event", {
        eventName,
        eventDate,
        eventLink,
        description: eventDescription,
      });
      alert("Event added successfully!");
    } catch (error) {
      console.error(error);
      alert("Failed to add event. Please try again.");
    }
  };

  return (
    <div>
      <h2>Welcome to the Admin Dashboard!</h2>

      {/* Navigation Links */}
      <div className="section-links">
        <ScrollLink
          to="addNewEvent"
          smooth={true}
          duration={500}
          onClick={() => setSelectedSection("addNewEvent")}
          className={`section-link ${
            selectedSection === "addNewEvent" && "active"
          }`}
        >
          Add New Event
        </ScrollLink>
        <ScrollLink
          to="editExistingEvent"
          smooth={true}
          duration={500}
          onClick={() => setSelectedSection("editExistingEvent")}
          className={`section-link ${
            selectedSection === "editExistingEvent" && "active"
          }`}
        >
          Edit Existing Event
        </ScrollLink>
        <ScrollLink
          to="announceResults"
          smooth={true}
          duration={500}
          onClick={() => setSelectedSection("announceResults")}
          className={`section-link ${
            selectedSection === "announceResults" && "active"
          }`}
        >
          Announce Results
        </ScrollLink>
      </div>

      {/* Add New Event Section */}
      <div id="addNewEvent">
        <h3>Add New Event</h3>
        <label>Event Name:</label>
        <input
          type="text"
          value={eventName}
          onChange={(e) => setEventName(e.target.value)}
        />

        <label>Event Date:</label>
        <input
          type="text"
          value={eventDate}
          onChange={(e) => setEventDate(e.target.value)}
        />

        <label>Event Link:</label>
        <input
          type="text"
          value={eventLink}
          onChange={(e) => setEventLink(e.target.value)}
        />

        <label>Event Description:</label>
        <textarea
          value={eventDescription}
          onChange={(e) => setEventDescription(e.target.value)}
        ></textarea>

        <button onClick={handleAddEvent}>Add Event</button>
      </div>

      {/* Edit Existing Event Section */}
      {selectedSection === "editExistingEvent" && (
        <div id="editExistingEvent">
          <h3>Edit Existing Event</h3>
          {/* Implement logic to fetch and display existing events for editing */}
          <form>
            <label>Select Event to Edit:</label>
            <select>
              {/* Populate the dropdown with existing events */}
              <option>Event 1</option>
              <option>Event 2</option>
            </select>

            <label>New Event Name:</label>
            <input type="text" />

            <label>New Event Date:</label>
            <input type="text" />

            <label>New Event Link:</label>
            <input type="text" />

            <label>New Event Description:</label>
            <textarea></textarea>

            <button type="submit">Edit Event</button>
          </form>
        </div>
      )}

      {/* Announce Results Section */}
      {selectedSection === "announceResults" && (
        <div id="announceResults">
          <h3>Announce Results</h3>
          {/* Implement logic to fetch and display past events for announcing results */}
          <form>
            <label>Select Event to Announce Results:</label>
            <select>
              {/* Populate the dropdown with past events */}
              <option>Event 1</option>
              <option>Event 2</option>
            </select>

            <label>Results Link:</label>
            <input type="text" />

            <button type="submit">Announce Results</button>
          </form>
        </div>
      )}
    </div>
  );
};

export default AdminDashboard;
